ASSUME CS:CODE,DS:DATA
DATA SEGMENT
OPR1 DW 0033H
OPR2 DW 0054H
MSG DB "THE SUM IS $"
DATA ENDS
CODE SEGMENT
START : MOV AX,DATA
        MOV DS,AX
        MOV DX,OFFSET MSG
        MOV AH,09
        INT 21H
        MOV AX,OPR1
        MOV BX,OPR2
        CLC
        ADD AX,BX
        MOV BL,AL
        MOV BH,BL
        AND BL,00F0H
        MOV CL,04
        ROR BL,CL
        MOV DL,BL
        ADD DL,30H
        MOV AH,02H
        INT 21H
        MOV BL,BH
        AND BL,0FH
        MOV DL,BL
        ADD DL,30H
        MOV AH,02H
        INT 21H
        MOV AH,CH
        INT 21H
        CODE ENDS
        END START
